sys_atf_step_config.description_generator:
function generateDescription() {
	// the global variable 'step' represents the current glide record
	var description = "This ATF step generates the current date/time and adds or subtracts a specified duration from it. The operation input determines whether to add or subtract from the current date/time. The duration input should be in the format 'x[d|h|m|s] y[d|h|m|s] ...', where 'x', 'y', etc. are the number of days, hours, minutes, or seconds to add or subtract from the current date/time. The output of this step is the new date/time as a string.";

	// your code here
	return description;
}
generateDescription();

sys_atf_step_config.step_execution_generator:
// Get the input values from the test step
var inputDuration = inputs.u_duration.toString();
var durationInput = new GlideDuration();
    durationInput.setValue(inputDuration);
var operationInput = inputs.u_operation.toString();

// Create a new GlideDateTime object for the current date/time
var currentDate = new GlideDateTime();

// Determine whether to add or subtract based on the operation input
if (operationInput === 'add') {
	currentDate.add(durationInput);
} else if (operationInput === 'subtract') {
	currentDate.subtract(durationInput);
}

// Create a step result object with the new date/time as the output value
outputs.u_newdatetime = currentDate.getDisplayValue();

// Create a step result object with a status of "success"
stepResult.setSuccess();
