sys_variable_value.value:
(function execute(inputs, outputs) {
  var ritmId = inputs.ritm_document_id;
  var variableName = inputs.variable_name;
  var variableValue = inputs.variable_value;
  if(ritmId){
    var ritmGr = new GlideRecord('sc_req_item');
    ritmGr.get(ritmId);
    ritmGr.variables[variableName] = variableValue;
    ritmGr.update();
  }
})(inputs, outputs);

