sys_variable_value.value:
(function execute(inputs, outputs) {
  var docId = inputs.ritmDocumentId;
  var varValue = inputs.varValue;
  var varName = inputs.varName;
  var ritmGr = new GlideRecord('sc_req_item');
  ritmGr.get(docId);
  ritmGr.variables[varName] = varValue;
  ritmGr.update()
})(inputs, outputs);

