sys_variable_value.value:
(function execute(inputs, outputs) {
  // Get the current date/time
  var now = new GlideDateTime();
  now = new GlideDateTime(now.getLocalDate() + ' ' +  now.getLocalTime().getByFormat('hh:mm:ss'));

  // Get the target date/time from the 'target_date' field
  var target = new GlideDateTime(inputs.target_date);

  //Default duration to 1 second
  var duration = "1970-01-01 00:00:01";
  // If the target is on or after now, then return the difference
  if (target.onOrAfter(now)) {
    // Convert the duration from milliseconds to duration format
    duration = GlideDateTime.subtract(now,target);
  }
  outputs.duration = duration;
})(inputs, outputs);

