sys_variable_value.value:
(function execute(inputs, outputs) {
  var targetDay = inputs.DayOfWeek.toString();
  var gdt = new GlideDateTime();
  var isTargetDay = false;
  var i = 0;
  var thisday = '';
  while(i<8 && !isTargetDay){
    gdt.addDaysLocalTime(1);
    thisDay = gdt.getDayOfWeekLocalTime().toString();
    isTargetDay = (thisDay==targetDay);
    i++;
  }
  outputs.GDT = gdt.getDate().toString();
})(inputs, outputs);

